-- ----------------------------
-- Table structure for t_journ
-- ----------------------------
DROP TABLE IF EXISTS `t_journ`;
CREATE TABLE `t_journ`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `date` datetime NULL DEFAULT NULL COMMENT '时间',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '标题',
  `content` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '文章内容',
  `type` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '类型',
  `status` int(11) NULL DEFAULT NULL COMMENT '状态 ： 0 审核中、1 已发布、2 已驳回',
  `rejectreason` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '驳回理由',
  `visited` bigint(255) NULL DEFAULT NULL COMMENT '访问数量',
  `delstatus` int(255) NULL DEFAULT NULL COMMENT '删除状态，0 未删除、1 已删除',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 15 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Compact;

