INSERT INTO `t_journ` VALUES (1, '2022-05-21 14:24:05', '小学生说国粹', '现在的小学生干什么不好，偏偏要去干小偷小摸的行为。\n去干就算了，被抓到还指着别人骂，搞得自己很言之有理一样，真是不知羞耻、厚脸皮，可恶至极，我们一定要抵制这些不良行为，积极传递正能量', '教育', 1, '', 75, 0);
INSERT INTO `t_journ` VALUES (2, '2002-06-14 07:25:01', '关于小学生说谎', '装修材料展现出路口\n那里吧zxcbzxchlkqadwnabvahxcnak.xn线程子类喜欢吃旅行者拿出来卡不够毒霸修理厂博览会留校察看现在不差', '神话', 2, '言语愚蠢至极，没有任何的价值，这种东西不能上新闻的\n洗洗睡吧', 0, 0);
